import boto3import botocoreimport pandas as pdimport os#print(boto3.__version__)#print(csv.__version__)#We need to have authentication credentialsdef authentication_credentials():    s3 = boto3.resource(        's3',        aws_access_key_id = 'AKIAISIQIAGM54JOQKVQ',        aws_secret_access_key = 'c3IJ/RlU1LKXvZCf0W51I2i4ILBkwZhw4rPv6LZw'    )    bucket = s3.Bucket('stock-data-inzynier')        return s3, bucketdef save_file(filename):    s3, bucket = authentication_credentials()    s3.Object('stock-data-inzynier', filename).upload_file(Filename=filename)        def check_if_file_exist(filename):    s3, bucket = authentication_credentials()        try:        s3.Object('stock-data-inzynier', filename).load()    except botocore.exceptions.ClientError as e:        if e.response['Error']['Code'] == "404":            return False        else:            return False    else:        return True    def append_to_csv(filename, data):        s3, bucket = authentication_credentials()        local_file_name = "/tmp/" + filename        bucket.download_file(filename, local_file_name)        with open(local_file_name, 'a') as csv_file:        data.to_csv(csv_file, mode='a', header=False)                         df = pd.read_csv(local_file_name, index_col=0)    df = df[~df.index.duplicated(keep='first')]    df.to_csv(local_file_name, float_format="%.4f")        bucket.upload_file(local_file_name, filename)        print('Appending data to csv successful.')    def get_csv_data_from_amazon(currency_name):        currency_data_file_name = currency_name + ".csv"    local_file_name = "/tmp/" + currency_data_file_name    if not os.path.isfile(local_file_name):        s3, bucket = authentication_credentials()        bucket.download_file(currency_data_file_name, local_file_name)        print("Downloading")    else:        print("File already exist")            data = pd.read_csv(local_file_name)        return data    